mapper.insert:
/* MemberMapper.insert */
INSERT INTO MEMBER (
       MEMBER_ID
     , NAME
     , SHIPPING_ADDRESS
     , PHONE_NUMBER
     , EMAIL
     , CREATED_AT
)
VALUES (
       NEXTVAL('MEMBER_ID_SEQ')
     , #{name}
     , #{shippingAddress}
     , #{phoneNumber}
     , #{email}
     , NOW()
)

mapper.update:
/* MemberMapper.update */
UPDATE MEMBER
   SET NAME = #{name}
     , SHIPPING_ADDRESS = #{shippingAddress}
     , PHONE_NUMBER = #{phoneNumber}
     , EMAIL = #{email}
 WHERE MEMBER_ID = #{memberId}

mapper.delete:
/* MemberMapper.delete */
DELETE
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
