mapper.insert:
/* OrderItemMapper.insert */
INSERT INTO ORDER_ITEM (
       ORDER_ITEM_ID
     , ORDER_ID
     , ORD_SEQ
     , ORD_PROC_SEQ
     , PRODUCT_ID
     , QUANTITY
     , PRICE_AT_ORDER
)
VALUES (
       NEXTVAL('ORDER_ITEM_ID_SEQ')
     , #{orderId}
     , #{ordSeq}
     , #{ordProcSeq}
     , #{productId}
     , #{quantity}
     , #{priceAtOrder}
)

mapper.update:
/* OrderItemMapper.update */
UPDATE ORDER_ITEM
   SET QUANTITY = #{quantity}
     , PRICE_AT_ORDER = #{priceAtOrder}
 WHERE ORDER_ITEM_ID = #{orderItemId}

mapper.delete:
/* OrderItemMapper.delete */
DELETE
  FROM ORDER_ITEM
 WHERE ORDER_ITEM_ID = #{orderItemId}
