mapper.update:
UPDATE orders
SET
    claim_id = #{claimId},
    total_amount = #{totalAmount},
    status = #{status, typeHandler=org.apache.ibatis.type.EnumTypeHandler}
WHERE order_id = #{orderId}
  AND ord_seq = #{ordSeq}
  AND ord_proc_seq = #{ordProcSeq}

mapper.delete:
DELETE FROM orders
WHERE order_id = #{orderId}
  AND ord_seq = #{ordSeq}
  AND ord_proc_seq = #{ordProcSeq}
