mapper.insert:
/* OrderMapper.insert */
INSERT INTO ORDERS (
       ORDER_ID
     , ORD_SEQ
     , ORD_PROC_SEQ
     , CLAIM_ID
     , MEMBER_ID
     , ORDER_DATE
     , TOTAL_AMOUNT
     , STATUS
)
VALUES (
       #{orderId}
     , #{ordSeq}
     , #{ordProcSeq}
     , #{claimId}
     , #{memberId}
     , #{orderDate}
     , #{totalAmount}
     , #{status}
)

mapper.update:
/* OrderMapper.update */
UPDATE ORDERS
   SET STATUS = #{status}
     , TOTAL_AMOUNT = #{totalAmount}
 WHERE ORDER_ID = #{orderId}
   AND ORD_SEQ = #{ordSeq}
   AND ORD_PROC_SEQ = #{ordProcSeq}

mapper.delete:
/* OrderMapper.delete */
DELETE
  FROM ORDERS
 WHERE ORDER_ID = #{orderId}
