mapper.insert:
/* PaymentMapper.insert */
INSERT INTO PAYMENT (
       PAYMENT_ID
     , MEMBER_ID
     , ORDER_ID
     , CLAIM_ID
     , AMOUNT
     , PAYMENT_METHOD
     , PAY_TYPE
     , PG_COMPANY
     , STATUS
     , ORDER_STATUS
     , TRANSACTION_ID
     , PAYMENT_DATE
)
VALUES (
       #{paymentId}
     , #{memberId}
     , #{orderId}
     , #{claimId}
     , #{amount}
     , #{paymentMethod}
     , #{payType}
     , #{pgCompany}
     , #{status}
     , #{orderStatus}
     , #{transactionId}
     , #{paymentDate}
)

mapper.update:
/* PaymentMapper.update */
UPDATE PAYMENT
   SET STATUS = #{status}
     , ORDER_STATUS = #{orderStatus}
     , TRANSACTION_ID = #{transactionId}
 WHERE PAYMENT_ID = #{paymentId}
   AND PAYMENT_METHOD = #{paymentMethod}
   AND ORDER_ID = #{orderId}
   AND PAY_TYPE = #{payType}

mapper.delete:
/* PaymentMapper.delete */
DELETE
  FROM PAYMENT
 WHERE PAYMENT_ID = #{paymentId}
   AND PAYMENT_METHOD = #{paymentMethod}
   AND ORDER_ID = #{orderId}
   AND PAY_TYPE = #{payType}
