mapper.insert:
/* PointHistoryMapper.insert */
INSERT INTO POINT_HISTORY (
       POINT_HISTORY_ID
     , MEMBER_ID
     , POINT_AMOUNT
     , BALANCE_AFTER
     , TRANSACTION_TYPE
     , REFERENCE_TYPE
     , REFERENCE_ID
     , DESCRIPTION
     , CREATED_AT
)
VALUES (
       NEXTVAL('POINT_HISTORY_ID_SEQ')
     , #{memberId}
     , #{pointAmount}
     , #{balanceAfter}
     , #{transactionType}
     , #{referenceType}
     , #{referenceId}
     , #{description}
     , NOW()
)

mapper.delete:
/* PointHistoryMapper.delete */
DELETE
  FROM POINT_HISTORY
 WHERE POINT_HISTORY_ID = #{pointHistoryId}
