mapper.insert:
/* ProductMapper.insert */
INSERT INTO PRODUCT (
       PRODUCT_ID
     , NAME
     , PRICE
)
VALUES (
       NEXTVAL('PRODUCT_ID_SEQ')
     , #{name}
     , #{price}
)

mapper.update:
/* ProductMapper.update */
UPDATE PRODUCT
   SET NAME = #{name}
     , PRICE = #{price}
 WHERE PRODUCT_ID = #{productId}

mapper.delete:
/* ProductMapper.delete */
DELETE
  FROM PRODUCT
 WHERE PRODUCT_ID = #{productId}
