mapper.insert:
INSERT INTO BASKET_BASE ( /* BasketBaseTrxMapper.insertBasketBase */
       BASKET_NO
     , MEMBER_NO
     , GOODS_NO
     , ITEM_NO
     , QUANTITY
     , IS_ORDER
)
VALUES (
       #{basketNo}
     , #{memberNo}
     , #{goodsNo}
     , #{itemNo}
     , #{quantity}
     , #{isOrder}
)

mapper.update:
UPDATE BASKET_BASE /* BasketBaseTrxMapper.updateBasketBase */
   SET MEMBER_NO = #{memberNo}
     , GOODS_NO = #{goodsNo}
     , ITEM_NO = #{itemNo}
     , QUANTITY = #{quantity}
     , IS_ORDER = #{isOrder}
 WHERE BASKET_NO = #{basketNo}

mapper.delete:
DELETE /* BasketBaseTrxMapper.deleteBasketBase */
  FROM BASKET_BASE
 WHERE BASKET_NO = #{basketNo}
