mapper.insert:
INSERT INTO CODE_BASE ( /* CodeBaseTrxMapper.insertCodeBase */
       GROUP_CODE
     , GROUP_CODE_NAME
     , REGIST_ID
     , REGIST_DATE_TIME
     , MODIFY_ID
     , MODIFY_DATE_TIME
)
VALUES (
       #{groupCode}
     , #{groupCodeName}
     , #{registId}
     , #{registDateTime}
     , #{modifyId}
     , #{modifyDateTime}
)

mapper.update:
UPDATE CODE_BASE /* CodeBaseTrxMapper.updateCodeBase */
   SET GROUP_CODE_NAME = #{groupCodeName}
     , MODIFY_ID = #{modifyId}
     , MODIFY_DATE_TIME = #{modifyDateTime}
 WHERE GROUP_CODE = #{groupCode}

mapper.delete:
DELETE /* CodeBaseTrxMapper.deleteCodeBase */
  FROM CODE_BASE
 WHERE GROUP_CODE = #{groupCode}
