     , DELIVERY_TYPE_CODE
     , ORDER_TYPE_CODE
     , ORDER_ACCEPT_DTM
     , ORDER_FINISH_DTM
)
VALUES (
       #{orderNo}
     , #{orderSequence}
     , #{orderProcessSequence}
     , #{upperOrderProcessSequence}
     , #{claimNo}
     , #{goodsNo}
     , #{itemNo}
     , #{quantity}
     , #{orderStatusCode}
