     , ORDER_TYPE_CODE = #{orderTypeCode}
     , ORDER_ACCEPT_DTM = #{orderAcceptDtm}
     , ORDER_FINISH_DTM = #{orderFinishDtm}
 WHERE ORDER_NO = #{orderNo}
   AND ORDER_SEQUENCE = #{orderSequence}
   AND ORDER_PROCESS_SEQUENCE = #{orderProcessSequence}

mapper.delete:
DELETE /* OrderDetailTrxMapper.deleteOrderDetail */
  FROM ORDER_DETAIL
 WHERE ORDER_NO = #{orderNo}
   AND ORDER_SEQUENCE = #{orderSequence}
