mapper.insert:
INSERT INTO ORDER_ITEM ( /* OrderItemMapper.insert */
       ORDER_ID
     , ORD_SEQ
     , ORD_PROC_SEQ
     , PRODUCT_ID
     , QUANTITY
     , PRICE_AT_ORDER
)
VALUES (
       #{orderId}
     , #{ordSeq}
     , #{ordProcSeq}
     , #{productId}
     , #{quantity}
     , #{priceAtOrder}
)

mapper.update:
UPDATE ORDER_ITEM /* OrderItemMapper.update */
   SET ORDER_ID = #{orderId}
     , ORD_SEQ = #{ordSeq}
     , ORD_PROC_SEQ = #{ordProcSeq}
     , PRODUCT_ID = #{productId}
     , QUANTITY = #{quantity}
     , PRICE_AT_ORDER = #{priceAtOrder}
 WHERE ORDER_ITEM_ID = #{orderItemId}

mapper.delete:
DELETE /* OrderItemMapper.delete */
  FROM ORDER_ITEM
 WHERE ORDER_ITEM_ID = #{orderItemId}
