mapper.insert:
INSERT INTO PAYMENT ( /* PaymentMapper.insert */
       PAYMENT_ID
     , MEMBER_ID
     , ORDER_ID
     , CLAIM_ID
     , AMOUNT
     , PAYMENT_METHOD
     , PAY_TYPE
     , PG_COMPANY
     , STATUS
     , ORDER_STATUS
     , TRANSACTION_ID
     , PAYMENT_DATE
)
VALUES (
       #{paymentId}
     , #{memberId}
     , #{orderId}
     , #{claimId}
     , #{amount}
     , #{paymentMethod}
     , #{payType}
     , #{pgCompany}
     , #{status}
     , #{orderStatus}
     , #{transactionId}
     , #{paymentDate}
)

mapper.update:
UPDATE PAYMENT /* PaymentMapper.update */
   SET MEMBER_ID = #{memberId}
     , ORDER_ID = #{orderId}
     , CLAIM_ID = #{claimId}
     , AMOUNT = #{amount}
     , PAYMENT_METHOD = #{paymentMethod}
     , PAY_TYPE = #{payType}
     , PG_COMPANY = #{pgCompany}
     , STATUS = #{status}
     , ORDER_STATUS = #{orderStatus}
     , TRANSACTION_ID = #{transactionId}
     , PAYMENT_DATE = #{paymentDate}
 WHERE PAYMENT_ID = #{paymentId}
   AND PAYMENT_METHOD = #{paymentMethod}
   AND ORDER_ID = #{orderId}
   AND PAY_TYPE = #{payType}

mapper.delete:
DELETE /* PaymentMapper.delete */
  FROM PAYMENT
 WHERE PAYMENT_ID = #{paymentId}
   AND PAYMENT_METHOD = #{paymentMethod}
   AND ORDER_ID = #{orderId}
   AND PAY_TYPE = #{payType}
