mapper.insert:
INSERT INTO REWARD_POINTS ( /* RewardPointsMapper.insert */
       MEMBER_ID
     , POINTS
     , LAST_UPDATED
)
VALUES (
       #{memberId}
     , #{points}
     , #{lastUpdated}
)

mapper.update:
UPDATE REWARD_POINTS /* RewardPointsMapper.update */
   SET MEMBER_ID = #{memberId}
     , POINTS = #{points}
     , LAST_UPDATED = #{lastUpdated}
 WHERE REWARD_POINTS_ID = #{rewardPointsId}

mapper.delete:
DELETE /* RewardPointsMapper.delete */
  FROM REWARD_POINTS
 WHERE REWARD_POINTS_ID = #{rewardPointsId}
